// App.js
import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';

function App() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'asc' });
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const loadData = async () => {
      try {
        const response = await window.fs.readFile('Liste Complète des Monstres par Zone (Quest Ocre).docx', { encoding: 'utf8' });
        const parsedData = parseMonsterData(response);
        setData(parsedData);
      } catch (error) {
        console.error('Erreur de chargement:', error);
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, []);

  const parseMonsterData = (text) => {
    const lines = text.split('\n');
    const parsedData = [];
    let currentZone = '';

    lines.forEach(line => {
      line = line.trim();
      if (!line) return;

      if (line.startsWith('#')) {
        currentZone = line.replace(/#/g, '').trim();
      } else if (line.startsWith('•')) {
        const monsterInfo = line.replace('•', '').trim();
        const matches = monsterInfo.match(/(.*?)\s*\((\d+)\)\s*$/);

        if (matches) {
          parsedData.push({
            zone: currentZone,
            monster: matches[1].trim(),
            level: parseInt(matches[2])
          });
        }
      }
    });

    return parsedData;
  };

  const exportToCSV = () => {
    const csv = Papa.unparse(data, {
      header: true,
      delimiter: ',',
      newline: '\n'
    });

    const blob = new Blob([csv], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    const url = URL.createObjectURL(blob);
    link.setAttribute('href', url);
    link.setAttribute('download', 'dofus_monsters.csv');
    link.style.visibility = 'hidden';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      Papa.parse(file, {
        header: true,
        complete: (results) => {
          const parsedData = results.data.map(row => ({
            zone: row.zone || row.Zone,
            monster: row.monster || row.Monster || row.Monstre,
            level: parseInt(row.level || row.Level || row.Niveau) || 0
          })).filter(row => row.monster && row.zone);
          setData(parsedData);
        },
        error: (error) => {
          console.error('Erreur de parsing CSV:', error);
        }
      });
    }
  };

  const handleSort = (key) => {
    setSortConfig(prev => ({
      key,
      direction: prev.key === key && prev.direction === 'asc' ? 'desc' : 'asc'
    }));
  };

  const filteredAndSortedData = React.useMemo(() => {
    let result = [...data];

    if (searchTerm) {
      result = result.filter(item =>
        Object.values(item).some(value =>
          value.toString().toLowerCase().includes(searchTerm.toLowerCase())
        )
      );
    }

    if (sortConfig.key) {
      result.sort((a, b) => {
        const direction = sortConfig.direction === 'asc' ? 1 : -1;

        if (typeof a[sortConfig.key] === 'number') {
          return (a[sortConfig.key] - b[sortConfig.key]) * direction;
        }

        return a[sortConfig.key].localeCompare(b[sortConfig.key]) * direction;
      });
    }

    return result;
  }, [data, searchTerm, sortConfig]);

  const stats = React.useMemo(() => {
    const uniqueZones = new Set(filteredAndSortedData.map(item => item.zone)).size;
    const totalMonsters = filteredAndSortedData.length;
    const averageLevel = Math.round(
      filteredAndSortedData.reduce((acc, curr) => acc + curr.level, 0) / totalMonsters
    );

    return { uniqueZones, totalMonsters, averageLevel };
  }, [filteredAndSortedData]);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <p className="text-xl">Chargement...</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 p-4">
      <div className="max-w-7xl mx-auto">
        {/* En-tête */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            Guide des Monstres Dofus
          </h1>
          <div className="flex gap-4 mb-4">
            <div>
              <input
                type="file"
                accept=".csv"
                onChange={handleFileUpload}
                className="hidden"
                id="csvInput"
              />
              <label
                htmlFor="csvInput"
                className="cursor-pointer bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
              >
                Importer CSV
              </label>
            </div>
            <button
              onClick={exportToCSV}
              className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
            >
              Exporter CSV
            </button>
          </div>
        </div>

        {/* Statistiques */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
          <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
            <h3 className="text-sm font-medium text-gray-500">Total Monstres</h3>
            <p className="text-2xl font-semibold text-blue-600">{stats.totalMonsters}</p>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
            <h3 className="text-sm font-medium text-gray-500">Zones Uniques</h3>
            <p className="text-2xl font-semibold text-green-600">{stats.uniqueZones}</p>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
            <h3 className="text-sm font-medium text-gray-500">Niveau Moyen</h3>
            <p className="text-2xl font-semibold text-purple-600">{stats.averageLevel}</p>
          </div>
        </div>

        {/* Barre de recherche */}
        <div className="mb-6">
          <input
            type="text"
            placeholder="Rechercher un monstre ou une zone..."
            className="w-full p-3 border rounded-lg shadow-sm focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        {/* Tableau */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 overflow-hidden">
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  {[
                    { key: 'zone', label: 'Zone' },
                    { key: 'monster', label: 'Monstre' },
                    { key: 'level', label: 'Niveau' }
                  ].map(({ key, label }) => (
                    <th
                      key={key}
                      onClick={() => handleSort(key)}
                      className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
                    >
                      <div className="flex items-center">
                        {label}
                        {sortConfig.key === key && (
                          <span className="ml-2">
                            {sortConfig.direction === 'asc' ? '↑' : '↓'}
                          </span>
                        )}
                      </div>
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {filteredAndSortedData.map((item, index) => (
                  <tr 
                    key={index}
                    className="hover:bg-gray-50 transition-colors"
                  >
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {item.zone}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {item.monster}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {item.level}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {/* Footer */}
        <div className="mt-4 text-center text-sm text-gray-500">
          Total affiché : {filteredAndSortedData.length} monstre(s)
        </div>
      </div>
    </div>
  );
}

export default App;
